.hentry .post-meta,
.hentry .post-meta a {
	color: #777;
	display: none;
}
.entry-title {
	font-size: 24px;
	margin-bottom: 12px;
	line-height: 32px;
	text-align: center;
}

#mainnav ul li a {
	display: inline-block;
    font-family: "Open Sans",sans-serif;
}

.roll-news .entry .title a {
    color: #cbcbcb;
}

h1, h2, h3, h4, h5, h6 {
    color: #ed761b;
    font-weight: 400;
    margin: 0 0 24px;
}

.hentry {
    padding: 30px;
    margin: 0 0 30px;
    background-color: #262626;
}

.post-grid .grid-items {
    margin: 0 auto;
    overflow: hidden;
    text-align: center;
    background-color: #262626;
}

.post-grid-234
{

    background: #262626
}

table > thead > tr > th, table > tbody > tr > th, table > tfoot > tr > th, table > thead > tr > td, table > tbody > tr > td, table > tfoot > tr > td {
    border: 1px solid #262626;
}

.btn-menu {
color: #ed761b;
}

.entry-thumb, .single-thumb {

    text-align: left;
}

iconok

table > thead > tr > th, table > tbody > tr > th, table > tfoot > tr > th, table > thead > tr > td, table > tbody > tr > td, table > tfoot > tr > td {
    padding: 1px;
    line-height: 1;
    vertical-align: top;
}


.roll-button.more-button {
    display: none;

}



.post-grid .item .layer-media img {
    border-style: solid;
    border-color: #ffffff;
    border-width: 2px;
    box-shadow: none;
    width: 100%;
}

.attachment-rocked-medium-thumb .size-rocked-medium-thumb .wp-post-image
{
    border-style: solid;
    border-color: #ffffff;
    border-width: 2px;
    box-shadow: none;
    width: 100%;
}
hirek nav
.comment-navigation .nav-next, .posts-navigation .nav-next, .post-navigation .nav-next {

    display: none;

}

.comment-navigation .nav-previous, .posts-navigation .nav-previous, .post-navigation .nav-previous {
    display: none;
}

a:hover { 
   color: #a09c96;
}

.post-grid .pagination.lite .page-numbers {

    line-height: 220%;
}

img {
    height: auto;
    max-width: 100%;
    padding: 20px;
}

